import React, { useState, useEffect } from 'react';
import './App.css';
import Track from './components/track';


function App() {

  const [token, setToken] = useState('');
  const [range, setRange] = useState('short_term');
  const [tracks, setTracks] = useState([])
  const [isLoggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    let urlstring = window.location.href;
    let url = new URL(urlstring);
    let c = url.searchParams.get('access_token');


    if (c) {
      setLoggedIn(true);
      setToken(c);
    }
  }, [])

  useEffect(() => {
    getTracks();
  }, [token, range])

  const getTracks = () => {
    fetch(`https://api.spotify.com/v1/me/top/tracks?time_range=${range}`, {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
      }),
    })
      .then(res => res.json())
      .then(data => setTracks(data.items));
  }

  const changeRange = (e) => {
    let { value } = e.target;
    setRange(value);
  }

  return (
    <div className="App">
      <div className="header">
        <h1 className="title">SPOTIFY TOP SONGS</h1>
        <select onChange={(e) => { changeRange(e) }}>
          <option value="short_term">4 weeks</option>
          <option value="medium_term">4 months</option>
          <option value="long_term">all time</option>
        </select>
      </div>
      {!isLoggedIn ? <a href="http://localhost:8888/login">login</a> :
        <div>
          {tracks && tracks.map((track, i) => {
            console.log(track);

            return (
              <Track
                rank={i + 1}
                key={track.id}
                name={track.name}
                popularity={track.popularity}
                album_images={track.album.images}
                artists={track.artists}
                url={track.preview_url}
              />
            )
          })}
        </div>
      }
    </div>
  );
}

export default App;
