import React, { useState, useEffect } from 'react';
import './App.css';

function useDebounce(value, delay) {
  // State and setters for debounced value
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(
    () => {
      // Update debounced value after delay
      const handler = setTimeout(() => {
        setDebouncedValue(value);
      }, delay);

      // Cancel the timeout if value changes (also on delay change or unmount)
      // This is how we prevent debounced value from updating if value is changed ...
      // .. within the delay period. Timeout gets cleared and restarted.
      return () => {
        clearTimeout(handler);
      };
    },
    [value, delay] // Only re-call effect if value or delay changes
  );

  return debouncedValue;
}


function App() {

  const [token, setToken] = useState('');
  const [isLoggedIn, setLoggedIn] = useState(false);

  const [query, setQuery] = useState('drake');
  const [tracks, setTracks] = useState([])
  const [isSearching, setIsSearching] = useState(false);
  const debouncedQuery = useDebounce(query, 500);

  // fetch the Spotify API token from the URL params.
  useEffect(() => {
    let urlstring = window.location.href;
    let url = new URL(urlstring);
    let c = url.searchParams.get('access_token');


    if (c) {
      setLoggedIn(true);
      setToken(c);
    }
  }, [])

  const searchSpotify = (q) => {
    return fetch(`https://api.spotify.com/v1/search/?q=${q}&type=track`, {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
      }),
    })
      .then(res => res.json())
  }

  // Effect for API call 
  useEffect(
    () => {
      if (debouncedQuery) {
        setIsSearching(true);
        searchSpotify(debouncedQuery).then(results => {
          setIsSearching(false);
          console.log(results.tracks.items)
          setTracks(results.tracks.items);
        });
      } else {
        setTracks([]);
      }
    },
    [debouncedQuery] // Only call effect if debounced search term changes
  );



  return (
    <div className="App">
      <div className="header">
        <h1 className="title">SPOTIFY SLOWED</h1>
      </div>
      {!isLoggedIn ? <a href="http://localhost:8888/login">login</a> :
        <div>
          <input
            type="text"
            placeholder="Search Spotify"
            onChange={e => setQuery(e.target.value)}
          />
          <button onClick={() => { searchSpotify() }}>Search Spotify</button>
          {isSearching && <div>Searching ...</div>}
          {tracks.map(result => (
            <div key={result.id}>
              <h4>{result.name}</h4>
              {/* <img
                src={`${result.album.images[0].url}`}
              /> */}
            </div>
          ))}
        </div>
      }

      <div class="space-y-1">
        <label id="listbox-label" class="block text-sm leading-5 font-medium text-gray-700">
          Assigned to
  </label>
        <div class="relative">
          <span class="inline-block w-full rounded-md shadow-sm">
            <button type="button" aria-haspopup="listbox" aria-expanded="true" aria-labelledby="listbox-label" class="cursor-default relative w-full rounded-md border border-gray-300 bg-white pl-3 pr-10 py-2 text-left focus:outline-none focus:shadow-outline-blue focus:border-blue-300 transition ease-in-out duration-150 sm:text-sm sm:leading-5">
              <span class="block truncate">
                Tom Cook
        </span>
              <span class="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                <svg class="h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="none" stroke="currentColor">
                  <path d="M7 7l3-3 3 3m0 6l-3 3-3-3" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round" />
                </svg>
              </span>
            </button>
          </span>


          <div class="absolute mt-1 w-full rounded-md bg-white shadow-lg">
            <ul tabindex="-1" role="listbox" aria-labelledby="listbox-label" aria-activedescendant="listbox-item-3" class="max-h-60 rounded-md py-1 text-base leading-6 shadow-xs overflow-auto focus:outline-none sm:text-sm sm:leading-5">

              <li id="listbox-option-0" role="option" class="text-gray-900 cursor-default select-none relative py-2 pl-3 pr-9">

                <span class="font-normal block truncate">
                  Wade Cooper
          </span>


                <span class="text-indigo-600 absolute inset-y-0 right-0 flex items-center pr-4">

                  <svg class="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fill-rule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clip-rule="evenodd" />
                  </svg>
                </span>
              </li>

            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
