import React, { useState, useEffect } from 'react';
import './App.css';


function App() {

  const [token, setToken] = useState('');
  const [isLoggedIn, setLoggedIn] = useState(false);

  // fetch the Spotify API token from the URL params.
  useEffect(() => {
    let urlstring = window.location.href;
    let url = new URL(urlstring);
    let c = url.searchParams.get('access_token');


    if (c) {
      setLoggedIn(true);
      setToken(c);
    }
  }, [])

  const searchSpotify = () => {
    fetch(`https://api.spotify.com/v1/me/top/tracks?time_range=${range}`, {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
      }),
    })
      .then(res => res.json())
      .then(data => setTracks(data.items));
  }


  const getTracks = () => {
    fetch(`https://api.spotify.com/v1/me/top/tracks?time_range=${range}`, {
      method: 'GET',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
      }),
    })
      .then(res => res.json())
      .then(data => setTracks(data.items));
  }


  return (
    <div className="App">
      <div className="header">
        <h1 className="title">SPOTIFY SLOWED</h1>
      </div>
      {!isLoggedIn ? <a href="http://localhost:8888/login">login</a> :
        <div>
          search goes here
        </div>
      }
    </div>
  );
}

export default App;
